  value,
  noop: true,
  finished: true,
  cancelled: false,
})

export const getFinishedResult = (
  value: any,
  finished: boolean,
  cancelled = false
) => ({
  value,
  finished,
  cancelled,
})

export const getCancelledResult = (value: any) => ({
  value,
  cancelled: true,
  finished: false,
})
