  active
    ? (target[$P] |= IS_ANIMATING | HAS_ANIMATED)
    : (target[$P] &= ~IS_ANIMATING)

export const setPausedBit = (target: any, paused: boolean) =>
  paused ? (target[$P] |= IS_PAUSED) : (target[$P] &= ~IS_PAUSED)
